#include <stdio.h>
#include <stdlib.h>
int main()
{
    int option, mat_num, courses, courseCredit, courseCreditTotal, grade, credit_points, matCreditTotal = 0, total_mats, matCredit = 0;
    printf("\n\n*********************************************************");
    printf("\nWelcome to CS.CODE.IN's Credit Score Analyser\n");
    printf("*********************************************************\n");
    printf("\nChoose from below Options:- ");
    printf("\n\nA) Press 1 to Calculate MGPA");
    printf("\nB) Press 2 to Calculate CGPA");
    printf("\nE) Press 0 to Exit ");
    printf("\n\nEnter your Input:  ");
    scanf("%d", &option);

    switch ((int)option)
    {
    case 1:
    {
        float internals, m1, total, mgpa;
        printf("\nEnter MAT Number:  ");
        scanf("%d", &mat_num);
        printf("\nEnter Number of Courses:  ");
        scanf("%d", &courses);
        for (int i = 1; i <= courses; i++)
        {
            printf("\nEnter credits alloted for course %d: ", i);
            scanf("%d", &courseCredit);
            courseCreditTotal += courseCredit;
        }
        printf("Total Credits = %d", courseCreditTotal);

        printf("\nEnter Marks Achieved in Internals: ");
        scanf("%f", &internals);
        printf("\nMarks Achieved in Internals: %.1f", internals);
        printf("\nEnter Marks Achieved in Final M1: ");
        scanf("%f", &m1);
        printf("\nMarks Achieved in Final M1: %.1f", m1);
        total = internals + m1;
        printf("\nTotal = %.1f", total);
        (total >= 50 && total < 60) ? (grade = 6) : (total >= 60 && total < 70) ? (grade = 7)
                                                : (total >= 70 && total < 80)   ? (grade = 8)
                                                : (total >= 80 && total < 90)   ? (grade = 9)
                                                : (total >= 90)                 ? (grade = 10)
                                                                                : (grade = 4.5);
        printf("\nGrade = %d", grade);
        credit_points = courseCreditTotal * grade;
        mgpa = credit_points / courseCreditTotal;
        printf("\nYour MGPA for Mat %d = %.1f", mat_num, mgpa);
    }
    break;

    case 2:
    {

        float total2, mgpa2, cgpa;
        printf("\nEnter Total Number of MAT's: ");
        scanf("%d", &total_mats);
        if (total_mats <= 12)
        {
            for (int i = 1; i <= total_mats; i++)
            {
                printf("\nEnter total Credits for MAT %d: ", i);
                scanf("%d", &matCredit);
                printf("\nEnter MGPA for MAT %d: ", i);
                scanf("%f", &mgpa2);
                total2 += matCredit * mgpa2;
                matCreditTotal += matCredit;
                cgpa = total2 / matCredit;
            }
            printf("\nTotal Credits into MGPA = %.1f", total2);
            printf("\nTotal MAT Credits = %d", matCreditTotal);

            printf("\nYour CGPA is %.1f (until mat %d)", cgpa, total_mats);
        }
        else
        {
            printf("Invalid Input!");
        }
    }
    break;
    case 0:
    {
        exit(0);
    }
    default:
        printf("Invalid Input!\a");
        break;
    }
}
